  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 1000);
    return () => clearInterval(interval);
  }, []);

  // Local countdown
  useEffect(() => {
    const countdown = setInterval(() => {
      setLocalTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);
    return () => clearInterval(countdown);
  }, []);

  const emptySlots = 5;
  const teamAPicks = [...teamA.picks, ...Array(emptySlots - teamA.picks.length).fill(null)];
  const teamBPicks = [...teamB.picks, ...Array(emptySlots - teamB.picks.length).fill(null)];

  const timerPercent = (localTimer / MAX_TIMER) * 100;
  const barColor = phase === "pick" ? "#4caf50" : "#f44336";

  return (
    <div className="draft-ui">
      <div className="draft-box">
        <div className="team team-left">
          <div className="bans">
            {teamA.bans.map((ban, i) => (
              <div key={ban + i} className="ban-img-wrapper">
                <img
                  src={getChampImg(ban)}
                  alt={ban}
                  className="ban-img slide-down"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = getChampImg(ban === "Wukong" ? "MonkeyKing" : ban === "LeBlanc" ? "Leblanc" : "Unknown");
                  }}
                />
              </div>
            ))}
          </div>
          <div style={{ textAlign: "center", color: "white", fontWeight: "bold", marginBottom: "5px" }}>
            {phase === "pick" ? "Pick Phase" : "Ban Phase"} - {localTimer}s
          </div>
          <div className="timer-bar-inside">
            <div className="timer-bar" style={{ width: `${timerPercent}%`, backgroundColor: barColor }} />
          </div>
          <div className="picks">
            {teamAPicks.map((champ, i) => (
              <div key={i} style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
                <div className="pick-slot">
                  {champ ? (
                    <img
                      src={getChampImg(champ)}
                      alt={champ}
                      className="champ-img slide-down"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = getChampImg(champ === "Wukong" ? "MonkeyKing" : champ === "LeBlanc" ? "Leblanc" : "Unknown");
                      }}
                    />
                  ) : (
                    <div className="champ-placeholder" />
                  )}
                  {champ && <img src={getRoleIcon(i)} alt="role" className="role-icon" />}
                </div>
                <span className="player-name">{teamA.players[i] || "-"}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="center">
          <div className="logos">
            <div className="logo">BLUE</div>
            <span className="vs">vs</span>
            <div className="logo">RED</div>
          </div>
          <div className="patch">PATCH 25.13</div>
        </div>

        <div className="team team-right">
          <div className="bans">
            {teamB.bans.map((ban, i) => (
              <div key={ban + i} className="ban-img-wrapper">
                <img
                  src={getChampImg(ban)}
                  alt={ban}
                  className="ban-img slide-down"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = getChampImg(ban === "Wukong" ? "MonkeyKing" : ban === "LeBlanc" ? "Leblanc" : "Unknown");
                  }}
                />
              </div>
            ))}
          </div>
          <div style={{ textAlign: "center", color: "white", fontWeight: "bold", marginBottom: "5px" }}>
            {phase === "pick" ? "Pick Phase" : "Ban Phase"} - {localTimer}s
          </div>
          <div className="timer-bar-inside">
            <div className="timer-bar" style={{ width: `${timerPercent}%`, backgroundColor: barColor }} />
          </div>
          <div className="picks">
            {teamBPicks.map((champ, i) => (
              <div key={i} style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
                <div className="pick-slot">
                  {champ ? (
                    <img
                      src={getChampImg(champ)}
                      alt={champ}
                      className="champ-img slide-down"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = getChampImg(champ === "Wukong" ? "MonkeyKing" : champ === "LeBlanc" ? "Leblanc" : "Unknown");
                      }}
                    />
                  ) : (
                    <div className="champ-placeholder" />
                  )}
                  {champ && <img src={getRoleIcon(i)} alt="role" className="role-icon" />}
                </div>
                <span className="player-name">{teamB.players[i] || "-"}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DraftUI;
